-- access_token
create table oauth_access_token
(
    token_id          varchar(255) null,
    token             blob         null,
    authentication_id varchar(255) null,
    user_name         varchar(255) null,
    client_id         varchar(255) null,
    authentication    blob         null,
    refresh_token     varchar(255) null
)
    charset = utf8;

create index authentication_id_index
    on oauth_access_token (authentication_id);

create index client_id_index
    on oauth_access_token (client_id);

create index refresh_token_index
    on oauth_access_token (refresh_token);

create index token_id_index
    on oauth_access_token (token_id);

create index user_name_index
    on oauth_access_token (user_name);

